${Gen-Snippet-LG1.ps1} = {function Create-Snippet{Measure-Command {$filename=Read-Host "Enter the name of the json input file";$fileout=Read-Host "Enter the name of the output js file (with ext.)";$Input=(Get-Content -Path ".\$($filename).json")|ConvertFrom-Json;for($l=0;$l -lt 5;$l++){echo ""};$Blank=(Get-Content -Path ".\$($fileout)");$Input|% {$Blank[$_.value-1]+=$_.txt+[System.Environment]::NewLine};$Blank|Set-Content -Path ".\$($fileout)"}};Create-Snippet}
