${Pdb-Exporter-LG1.ps1} = {$initializeDir="{0}\Documents\Visual Studio 2015\Projects\"-f$env:USERPROFILE;$excUtil="{0}\Downloads\Strings\strings.exe"-f$env:USERPROFILE;$exewd="{0}\Downloads\Strings\"-f$env:USERPROFILE;$exportedPdbs="{0}\Documents\all-pdbs.export"-f$env:USERPROFILE;$exporteddata="{0}\Documents\pdbtext.out"-f$env:USERPROFILE;$basedir="{0}\Documents\"-f$env:USERPROFILE;$csfiles="$($basedir)sample_CSSolfiles.txt";$nspaces="$($basedir)sample_NSusings.txt";$constrInst="$($basedir)sample_Constr.txt";$delInst="$($basedir)sample_deleg.txt";$BPointProgr="$($basedir)sample_breakp.txt";Get-ChildItem -LiteralPath $initializeDir -Recurse -File|Where-Object {$_.FullName-match".pdb"}|% {$_.FUllName}|Set-Content -LiteralPath $exportedPdbs;Start-Process "notepad.exe" -ArgumentList $exportedPdbs;$lineNumInput=Read-Host "Enter the line number of the file to process in the list of PDB files..";$selpdb=(Get-Content $exportedPdbs|Select-String -AllMatches ".pdb"|Select -Index ($lineNumInput - 1));$options="-nobanner `"$($selpdb)`" > $($exporteddata)";Measure-Command {Start-Process $env:ComSpec -WorkingDirectory $exewd -ArgumentList "/C $($excUtil) $options" -WindowStyle Hidden};Write-Host "" `r`n;[System.Diagnostics.Process]::Start.Invoke($excUtil,"$options");Get-Content -LiteralPath $exporteddata|ConvertFrom-String -TemplateFile $csfiles|Format-Table;Get-Content -LiteralPath $exporteddata|ConvertFrom-String -TemplateFile $nspaces|Format-Table;Get-Content -LiteralPath $exporteddata|ConvertFrom-String -TemplateFile $constrInst|Format-Table;Get-Content -LiteralPath $exporteddata|ConvertFrom-String -TemplateFile $delInst|Format-Table;Get-Content -LiteralPath $exporteddata|ConvertFrom-String -TemplateFile $BPointProgr|Format-Table}
